-- reservation.sql
DROP TABLE RESERVATION CASCADE CONSTRAINTS;

CREATE TABLE RESERVATION (
    reservation_id NUMBER(10) PRIMARY KEY,
    reservation_date DATE NOT NULL,
    reservation_time TIMESTAMP NOT NULL,
    status VARCHAR2(20) DEFAULT 'pending' NOT NULL,
    customer_id NUMBER(10) NOT NULL,
    table_id NUMBER(10) NOT NULL,
    branch_id NUMBER(10) NOT NULL,
    CONSTRAINT fk_customer_1
        FOREIGN KEY (customer_id)
        REFERENCES CUSTOMER(customer_id)
        ON DELETE CASCADE,
    CONSTRAINT fk_table_1
        FOREIGN KEY (table_id)
        REFERENCES RES_TABLE(table_id)
        ON DELETE CASCADE,
    CONSTRAINT fk_branch_1
        FOREIGN KEY (branch_id)
        REFERENCES BRANCH(branch_id)
        ON DELETE CASCADE,
    CONSTRAINT chk_status_1
        CHECK (status IN ('pending', 'confirmed', 'cancelled'))
);


COMMIT;
